import React, { Component } from 'react';
import { Link } from 'react-router-dom'


class Navbar extends Component {
    constructor() {
        super();
        this.state = {
            isProductToggle: false,
            isCommunityToggle: false,
        }

        this.setWrapperRef = this.setWrapperRef.bind(this);
        this.handleClickOutside = this.handleClickOutside.bind(this);
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }

    setWrapperRef(node) {
        this.wrapperRef = node;
    }

    handleClickOutside(event) {
        if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
            this.setState({ isProductToggle: false })
            this.setState({ isCommunityToggle: false })
        }
    }

    handleToggleProduct = () => {
        this.setState({
            isProductToggle: !this.state.isProductToggle,
            isCommunityToggle: false
        })

    }

    handleToggleCommunity = () => {
        this.setState({
            isCommunityToggle: !this.state.isCommunityToggle,
            isProductToggle: false,
        })
    }

    render() {
        return (
            <React.Fragment>
                <div className="menuBack">
                    <div className="ui secondary container menu ">
                        <Link to="/">
                            <img src="/img/logo/logo-white.png" className="ui middle aligned image logo" alt="kramaos" />
                        </Link>
                        <div className="right menu navbar navbar-purple">

                            <div className="ui pointing dropdown link item"
                                ref={this.setWrapperRef}
                                onClick={this.handleToggleProduct}>
                                <span className="text">Products</span>
                                <i className="dropdown icon"></i>


                                <div className={this.state.isProductToggle ? "menu productsDropdown transition visible" : "menu productsDropdown transition hidden"}>
                                    <a className="item navbar-Koompi" href="https://koompi.com/" target="_blank" rel="noopener noreferrer"><img src="img/icons/koompi-stroke.svg" alt="kramaos" /><span>Hardware-KOOMPI</span></a>
                                    <Link className="item navbar-Koompi" to="/kosmos"><img src="img/icons/kosmos-stroke.svg" alt="kramaos" /><span>OS-KOSMOS</span></Link>
                                </div>
                            </div>


                            <div className="ui pointing dropdown link item"
                                ref={this.setWrapperRef}
                                onClick={this.handleToggleCommunity}>
                                <span className="text">Community</span>
                                <i className="dropdown icon"></i>
                                <div className={this.state.isCommunityToggle ? "menu productsDropdown transition visible" : "menu productsDropdown transition hidden"}>
                                    <a className="item navbar-Koompi" href="https://forum.koompi.com/" target="_blank" rel="noopener noreferrer"><img src="img/icons/faq-01.svg" alt="kramaos" /><span>Question and Answer</span></a>
                                    <a className="item navbar-Koompi" href="https://lab.krama.org" target="_blank" rel="noopener noreferrer"><img src="img/icons/project.svg" alt="kramaos" /><span>Projects</span></a>
                                </div>
                            </div>
                            {/* <a className="item bars-icon" href="#"><i className="bars icon"></i></a> */}

                        </div>

                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Navbar;
